import proton_app_uniffi
import Testing

@testable import ProtonMail

final class LegacyMigrationServiceTests {
    private let legacyKeychain = LegacyKeychain.randomInstance()
    private let mailSessionStub = MailSessionSpy()
    private let testUserDefaults = TestableUserDefaults.randomInstance()
    private var recordedCallsToMigrate: [[MigrationData]] = []

    private lazy var sut = LegacyMigrationService(
        legacyKeychain: legacyKeychain,
        legacyDataProvider: .init(userDefaults: testUserDefaults),
        getMailSession: { [unowned self] in mailSessionStub },
        passMigrationPayloadsToRustSDK: { [unowned self] in recordedCallsToMigrate.append($0) }
    )

    deinit {
        legacyKeychain.removeEverything()
        testUserDefaults.removePersistentDomain(forName: testUserDefaults.suiteName)
    }

    @Test
    func ifMainKeyAndAuthCredentialsAreFound_performsTheMigration() async throws {
        try seedMainKeyOfTheOldAppInLegacyKeychain()
        seedAuthCredentialsInLegacyUserDefaults()
        seedUserInfosInLegacyUserDefaults()

        await sut.proceed()

        #expect(recordedCallsToMigrate == [[expectedMigrationPayload]])
        await #expect(currentState() == .notNeeded)
    }

    @Test
    func ifCantFindLegacyData_doesntPerformTheMigration() async throws {
        try seedMainKeyOfTheOldAppInLegacyKeychain()

        await sut.proceed()

        #expect(recordedCallsToMigrate.count == 0)
        await #expect(currentState() == .notNeeded)
    }

    @Test
    func performingMigrationMoreThanOnceHasNoEffect() async throws {
        try seedMainKeyOfTheOldAppInLegacyKeychain()
        seedAuthCredentialsInLegacyUserDefaults()
        seedUserInfosInLegacyUserDefaults()

        await sut.proceed()
        await sut.proceed()

        #expect(recordedCallsToMigrate.count == 1)
        await #expect(currentState() == .notNeeded)
    }

    @Test
    func migrationIncludesSettings() async throws {
        try seedMainKeyOfTheOldAppInLegacyKeychain()
        seedAuthCredentialsInLegacyUserDefaults()
        seedUserInfosInLegacyUserDefaults()

        await sut.proceed()

        #expect(mailSessionStub.changeAppSettingsInvocations.count == 1)
    }

    // MARK: awaiting main key

    @Test
    func ifCantFindUnprotectedMainKey_waitsForItToBePassedFromOutside() async throws {
        seedAuthCredentialsInLegacyUserDefaults()
        seedUserInfosInLegacyUserDefaults()

        await sut.proceed()

        #expect(recordedCallsToMigrate.count == 0)

        let currentState = await currentState()

        switch currentState {
        case .awaitingProtectedMainKey:
            break
        default:
            Issue.record("Unexpected state at the end: \(currentState)")
        }
    }

    @Test
    func givenIsWaitingForMainKey_whenProvidedWithIt_thenResumesMigration() async throws {
        seedAuthCredentialsInLegacyUserDefaults()
        seedUserInfosInLegacyUserDefaults()

        await sut.proceed()
        await sut.resume(protectedMainKey: mainKey, protectionPreference: .biometrics)

        #expect(recordedCallsToMigrate.count == 1)
        await #expect(currentState() == .notNeeded)
    }

    @Test
    func givenIsWaitingForMainKey_whenProvidedWithIt_thenPassesOnProtectionUsedToUnlockIt() async throws {
        seedAuthCredentialsInLegacyUserDefaults()
        seedUserInfosInLegacyUserDefaults()

        await sut.proceed()
        await sut.resume(protectedMainKey: mainKey, protectionPreference: .pin("1234"))

        let pinThatWasSet = try #require(mailSessionStub.setPinCodeInvocations.first)
        #expect([UInt8](pinThatWasSet) == [1, 2, 3, 4])
    }

    @Test
    func givenIsWaitingForMainKey_whenAborted_thenDoesntPerformMigration() async throws {
        seedAuthCredentialsInLegacyUserDefaults()
        seedUserInfosInLegacyUserDefaults()

        await sut.proceed()
        await sut.abortWithoutProvidingProtectedMainKey()

        #expect(recordedCallsToMigrate.count == 0)
        await #expect(currentState() == .notNeeded)
    }

    private func seedMainKeyOfTheOldAppInLegacyKeychain() throws {
        try legacyKeychain.set(mainKey, forKey: .unprotectedMainKey)
    }

    private func seedAuthCredentialsInLegacyUserDefaults() {
        testUserDefaults.set(encryptedAuthCredentials, forKey: .authCredentials)
    }

    private func seedUserInfosInLegacyUserDefaults() {
        testUserDefaults.set(encryptedUserInfos, forKey: .userInfos)
    }

    private func currentState() async -> LegacyMigrationService.MigrationState {
        await sut.statePublisher.values.first { _ in true }.unsafelyUnwrapped
    }
}

private extension LegacyMigrationServiceTests {
    var mainKey: Data {
        .init(base64Encoded: "HgSNi+mB5OkfzS4sfMQ7FAMXtgK78UoDGMrtKMDDnK0=").unsafelyUnwrapped
    }

    var encryptedAuthCredentials: Data {
        .init(base64Encoded: "").unsafelyUnwrapped
    }

    var encryptedUserInfos: Data {
        .init(base64Encoded: "").unsafelyUnwrapped
    }

    var expectedMigrationPayload: MigrationData {
        .init(
            username: "kraqtest",
            displayName: "",
            primaryAddr: "kraqtest@proton.me",
            keySecret: "ACAwX00xKSL33dGQcsX3kCXqxPOx1xK",
            userId: "zACrNDn8UdlQ-xLFRotHuIS6aLJzvgp_mP1P8xqvfTbrbdoE6aHfJ-6sdR2LqA708WXkGLCAHxdKAYPNxLEhsg==",
            sessionId: "p5xecxmtgb5trouowcug6qsp5mcq7nii",
            passwordMode: .one,
            refreshToken: "uqj4sxr7p7tntmzvtpfhg5ne5vqlnd5x"
        )
    }
}
